require 'net/smtp'

# Description: Sublayer::Action responsible for sending an email using a specified SMTP server.
# This action can be used for automated notifications, reports, or follow-ups generated by AI processes.
#
# It is initialized with recipient email, subject, body, and optional SMTP server settings.
# It returns true if the email was sent successfully, false otherwise.
#
# Example usage: When you want to send an email notification or report based on AI-generated content.

class EmailSendAction < Sublayer::Actions::Base
  def initialize(to:, subject:, body:, from: ENV['DEFAULT_FROM_EMAIL'], 
                 smtp_address: ENV['SMTP_ADDRESS'], 
                 smtp_port: ENV['SMTP_PORT'], 
                 smtp_username: ENV['SMTP_USERNAME'], 
                 smtp_password: ENV['SMTP_PASSWORD'])
    @to = to
    @subject = subject
    @body = body
    @from = from
    @smtp_address = smtp_address
    @smtp_port = smtp_port.to_i
    @smtp_username = smtp_username
    @smtp_password = smtp_password
  end

  def call
    begin
      send_email
      Sublayer.configuration.logger.log(:info, "Email sent successfully to #{@to}")
      true
    rescue StandardError => e
      error_message = "Error sending email: #{e.message}"
      Sublayer.configuration.logger.log(:error, error_message)
      false
    end
  end

  private

  def send_email
    message = <<~MESSAGE
      From: #{@from}
      To: #{@to}
      Subject: #{@subject}

      #{@body}
    MESSAGE

    Net::SMTP.start(@smtp_address, @smtp_port, 'localhost', @smtp_username, @smtp_password, :login) do |smtp|
      smtp.send_message message, @from, @to
    end
  end
end
