require 'net/smtp'

# Description: Sublayer::Action responsible for sending an email using a specified SMTP server.
# This action can be used to send notifications, reports, or updates generated by AI processes.
#
# It is initialized with the necessary email details including SMTP settings.
# It returns true if the email was sent successfully, false otherwise.
#
# Example usage: When you want to send an email notification or report from an AI-driven process.

class EmailSendAction < Sublayer::Actions::Base
  def initialize(to:, from:, subject:, body:, smtp_server:, smtp_port:, smtp_username:, smtp_password:)
    @to = to
    @from = from
    @subject = subject
    @body = body
    @smtp_server = smtp_server
    @smtp_port = smtp_port
    @smtp_username = smtp_username
    @smtp_password = smtp_password
  end

  def call
    begin
      message = compose_message
      send_email(message)
      Sublayer.configuration.logger.log(:info, "Email sent successfully to #{@to}")
      true
    rescue StandardError => e
      error_message = "Error sending email: #{e.message}"
      Sublayer.configuration.logger.log(:error, error_message)
      false
    end
  end

  private

  def compose_message
    message = <<~MESSAGE
      From: #{@from}
      To: #{@to}
      Subject: #{@subject}

      #{@body}
    MESSAGE
    message
  end

  def send_email(message)
    Net::SMTP.start(@smtp_server, @smtp_port, 'localhost', @smtp_username, @smtp_password, :login) do |smtp|
      smtp.send_message(message, @from, @to)
    end
  end
end
